import React from 'react';
import {
  Dialog, DialogTitle, DialogContent, DialogActions, Button, TextField, Slider, Box
} from '@mui/material';

function EditDeviceDialog({ device, onClose, onUpdate }) {
  const handleChange = (field, value) => {
    onUpdate({ ...device, [field]: value });
  };

  return (
    <Dialog open={!!device} onClose={onClose}>
      <DialogTitle>Edit Device</DialogTitle>
      <DialogContent>
        <Box sx={{ mt: 2 }}>
          <TextField label="Device Name" value={device.name} onChange={(e) => handleChange('name', e.target.value)} fullWidth />
          <Slider value={device.waterFlow} onChange={(e, val) => handleChange('waterFlow', val)} min={0} max={500} step={1} />
          <Slider value={device.pressure} onChange={(e, val) => handleChange('pressure', val)} min={0} max={150} step={1} />
          <Slider value={device.temperature} onChange={(e, val) => handleChange('temperature', val)} min={-20} max={100} step={1} />
        </Box>
      </DialogContent>
      <DialogActions>
        <Button onClick={onClose}>Close</Button>
      </DialogActions>
    </Dialog>
  );
}

export default EditDeviceDialog;
