import React, { useState, useEffect } from 'react';
import {
  Box,
  Typography,
  CircularProgress,
  Button,
  TextField
} from '@mui/material';
import { Line, Bar, Pie } from 'react-chartjs-2';
import { Chart as ChartJS, CategoryScale, LinearScale, PointElement, LineElement, BarElement, ArcElement, Title, Tooltip, Legend } from 'chart.js';
import { LocalizationProvider, DatePicker } from '@mui/x-date-pickers';
import AdapterDateFns from '@mui/x-date-pickers/AdapterDateFns';

ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, BarElement, ArcElement, Title, Tooltip, Legend);

function DeviceAnalytics({ deviceId }) {
  const [loading, setLoading] = useState(true);
  const [chartData, setChartData] = useState({});
  const [chartType, setChartType] = useState('line');
  const [startDate, setStartDate] = useState(null);
  const [endDate, setEndDate] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      setLoading(true);
      try {
        let query = `http://localhost:5001/api/devices/${deviceId}`;
        if (startDate && endDate) {
          query += `?start=${startDate.toISOString()}&end=${endDate.toISOString()}`;
        }
        const response = await fetch(query);
        const device = await response.json();
        const labels = device.timestamps || Array.from(new Array(24), (_, index) => `${index} hour`);

        setChartData({
          labels,
          datasets: [
            {
              label: 'Water Flow (L/s)',
              data: device.waterFlow || [],
              borderColor: 'rgb(75, 192, 192)',
              backgroundColor: 'rgba(75, 192, 192, 0.5)',
            },
            {
              label: 'Pressure (psi)',
              data: device.pressure || [],
              borderColor: 'rgb(255, 99, 132)',
              backgroundColor: 'rgba(255, 99, 132, 0.5)',
            },
            {
              label: 'Temperature (°C)',
              data: device.temperature || [],
              borderColor: 'rgb(53, 162, 235)',
              backgroundColor: 'rgba(53, 162, 235, 0.5)',
            }
          ]
        });
      } catch (error) {
        console.error("Error fetching device data:", error);
      }
      setLoading(false);
    };

    fetchData();
  }, [deviceId, startDate, endDate]);

  const changeChart = (type) => {
    setChartType(type);
  };

  if (loading) {
    return <Box sx={{ display: 'flex', justifyContent: 'center' }}><CircularProgress /></Box>;
  }

  return (
    <LocalizationProvider dateAdapter={AdapterDateFns}>
      <Box>
        <Typography variant="h6" gutterBottom>
          Device Analytics: {deviceId}
        </Typography>
        <DatePicker
          label="Start Date"
          value={startDate}
          onChange={setStartDate}
          renderInput={(params) => <TextField {...params} />}
        />
        <DatePicker
          label="End Date"
          value={endDate}
          onChange={setEndDate}
          renderInput={(params) => <TextField {...params} />}
        />
        <Button variant="contained" onClick={() => changeChart('line')} sx={{ mr: 1 }}>Line Chart</Button>
        <Button variant="contained" onClick={() => changeChart('bar')} sx={{ mr: 1 }}>Bar Chart</Button>
        <Button variant="contained" onClick={() => changeChart('pie')}>Pie Chart</Button>
        {chartType === 'line' && <Line data={chartData} />}
        {chartType === 'bar' && <Bar data={chartData} />}
        {chartType === 'pie' && <Pie data={chartData} />}
      </Box>
    </LocalizationProvider>
  );
}

export default DeviceAnalytics;
