import React, { useEffect, useState } from 'react';
import {
  Container,
  Typography,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Button,
  Slider,
  Box,
  TextField,
  Stack
} from '@mui/material';

function App() {
  // Start with an empty array; we’ll fetch from the DB on mount
  const [devices, setDevices] = useState([]);

  // States for Editing Sensor Data Dialog
  const [openDialog, setOpenDialog] = useState(false);
  const [selectedDeviceIndex, setSelectedDeviceIndex] = useState(null);

  // States for Adding a New Device
  const [openAddDialog, setOpenAddDialog] = useState(false);
  const [newDevice, setNewDevice] = useState({
    Did: '',
    location: '',
    waterFlow: 0,
    pressure: 0,
    temperature: 0
  });

  /**
   * 1) Fetch from database on first render
   */
  useEffect(() => {
    // Adjust the URL to match your server’s endpoint
    fetch('http://localhost:5001/api/devices')
      .then((res) => res.json())
      .then((data) => {
        // `data` should be an array of devices from your DB
        setDevices(data);
      })
      .catch((err) => {
        console.error('Error fetching devices:', err);
      });
  }, []);

  /**
   * 2) Random update effect (every second)
   *    If you want to remove randomization, delete or modify this effect.
   */
  useEffect(() => {
    const interval = setInterval(() => {
      setDevices((prevDevices) =>
        prevDevices.map((device) => ({
          ...device,
          waterFlow: parseFloat((device.waterFlow + (Math.random() * 2 - 1)).toFixed(2)),
          pressure: parseFloat((device.pressure + (Math.random() * 2 - 1)).toFixed(2)),
          temperature: parseFloat((device.temperature + (Math.random() * 2 - 1)).toFixed(2))
        }))
      );
    }, 1000);

    return () => clearInterval(interval);
  }, []);

  // ---------------------------------------
  // Handlers for the sensor-editing dialog
  // ---------------------------------------
  const handleRowClick = (index) => {
    setSelectedDeviceIndex(index);
    setOpenDialog(true);
  };

  const handleDialogClose = () => {
    setOpenDialog(false);
  };

  // Handle slider changes for the selected device
  const handleSliderChange = (field, newValue) => {
    setDevices((prevDevices) => {
      const updated = [...prevDevices];
      updated[selectedDeviceIndex] = {
        ...updated[selectedDeviceIndex],
        [field]: newValue
      };
      return updated;
    });
  };

  // Convenience reference for the device being edited
  const selectedDevice =
    selectedDeviceIndex !== null ? devices[selectedDeviceIndex] : null;

  // ---------------------------------------
  // Handlers for the "Add Device" dialog
  // ---------------------------------------
  const handleOpenAddDialog = () => {
    setNewDevice({
      Did: '',
      location: '',
      waterFlow: 0,
      pressure: 0,
      temperature: 0
    });
    setOpenAddDialog(true);
  };

  const handleCloseAddDialog = () => {
    setOpenAddDialog(false);
  };

  // Add new device locally + POST to server
  const handleAddDevice = () => {
    // 1) Add locally
    setDevices((prevDevices) => [...prevDevices, newDevice]);

    // 2) (Optional) Post to server
    fetch('http://localhost:5001/api/devices', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(newDevice),
    })
      .then((res) => res.json())
      .then((data) => {
        console.log('Server response:', data);
      })
      .catch((error) => console.error('Error creating device:', error));

    // 3) Close dialog
    setOpenAddDialog(false);
  };

  // ---------------------------------------
  // Render
  // ---------------------------------------
  return (
    <Container maxWidth="md" sx={{ marginTop: 4 }}>
      <Typography variant="h4" gutterBottom>
        Water Management System
      </Typography>

      {/* Button to open "Add Device" dialog */}
      <Button
        variant="contained"
        sx={{ mb: 2 }}
        onClick={handleOpenAddDialog}
      >
        Add Device
      </Button>

      {/* Table of current devices */}
      <TableContainer component={Paper}>
        <Table>
          <TableHead>
            <TableRow>
              <TableCell><strong>Device ID</strong></TableCell>
              <TableCell><strong>Location</strong></TableCell>
              <TableCell><strong>Water Flow (L/s)</strong></TableCell>
              <TableCell><strong>Pressure (psi)</strong></TableCell>
              <TableCell><strong>Temperature (°C)</strong></TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {devices.map((device, index) => (
              <TableRow
                key={device.Did}
                hover
                style={{ cursor: 'pointer' }}
                onClick={() => handleRowClick(index)}
              >
                <TableCell>{device.Did}</TableCell>
                <TableCell>{device.location}</TableCell>
                <TableCell>{device.waterFlow}</TableCell>
                <TableCell>{device.pressure}</TableCell>
                <TableCell>{device.temperature}</TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>

      {/* Dialog for adjusting existing device sensor data */}
      <Dialog open={openDialog} onClose={handleDialogClose} fullWidth maxWidth="sm">
        <DialogTitle>Adjust Sensor Data</DialogTitle>
        <DialogContent dividers>
          {selectedDevice && (
            <>
              <Box sx={{ mt: 2 }}>
                <Typography gutterBottom>Water Flow (L/s)</Typography>
                <Slider
                  value={selectedDevice.waterFlow}
                  min={0}
                  max={500}
                  step={1}
                  onChange={(e, val) => handleSliderChange('waterFlow', val)}
                />
                <Typography variant="body2">
                  Current: {selectedDevice.waterFlow.toFixed(2)}
                </Typography>
              </Box>

              <Box sx={{ mt: 4 }}>
                <Typography gutterBottom>Pressure (psi)</Typography>
                <Slider
                  value={selectedDevice.pressure}
                  min={0}
                  max={150}
                  step={1}
                  onChange={(e, val) => handleSliderChange('pressure', val)}
                />
                <Typography variant="body2">
                  Current: {selectedDevice.pressure.toFixed(2)}
                </Typography>
              </Box>

              <Box sx={{ mt: 4 }}>
                <Typography gutterBottom>Temperature (°C)</Typography>
                <Slider
                  value={selectedDevice.temperature}
                  min={0}
                  max={100}
                  step={1}
                  onChange={(e, val) => handleSliderChange('temperature', val)}
                />
                <Typography variant="body2">
                  Current: {selectedDevice.temperature.toFixed(2)}
                </Typography>
              </Box>
            </>
          )}
        </DialogContent>
        <DialogActions>
          <Button onClick={handleDialogClose} variant="contained">
            Close
          </Button>
        </DialogActions>
      </Dialog>

      {/* Dialog for ADDING a new device */}
      <Dialog open={openAddDialog} onClose={handleCloseAddDialog} fullWidth maxWidth="sm">
        <DialogTitle>Add New Device</DialogTitle>
        <DialogContent dividers>
          {/* Input fields for new device */}
          <Stack spacing={2} sx={{ mt: 2 }}>
            <TextField
              label="Device ID"
              value={newDevice.Did}
              onChange={(e) => setNewDevice({ ...newDevice, Did: e.target.value })}
              fullWidth
            />
            <TextField
              label="Location"
              value={newDevice.location}
              onChange={(e) => setNewDevice({ ...newDevice, location: e.target.value })}
              fullWidth
            />
            <TextField
              label="Water Flow (L/s)"
              type="number"
              value={newDevice.waterFlow}
              onChange={(e) => setNewDevice({ ...newDevice, waterFlow: parseFloat(e.target.value) || 0 })}
              fullWidth
            />
            <TextField
              label="Pressure (psi)"
              type="number"
              value={newDevice.pressure}
              onChange={(e) => setNewDevice({ ...newDevice, pressure: parseFloat(e.target.value) || 0 })}
              fullWidth
            />
            <TextField
              label="Temperature (°C)"
              type="number"
              value={newDevice.temperature}
              onChange={(e) => setNewDevice({ ...newDevice, temperature: parseFloat(e.target.value) || 0 })}
              fullWidth
            />
          </Stack>
        </DialogContent>
        <DialogActions>
          <Button onClick={handleCloseAddDialog} variant="outlined">
            Cancel
          </Button>
          <Button onClick={handleAddDevice} variant="contained">
            Add
          </Button>
        </DialogActions>
      </Dialog>
    </Container>
  );
}

export default App;
