import React, { useState, useEffect, useCallback } from "react";
import { useParams } from "react-router-dom";
import { Box, Typography, Button, Container } from "@mui/material";
import API from "../api";

const CourseViewer = () => {
  const { courseId } = useParams();
  const [course, setCourse] = useState(null);
  const [currentPdfIndex, setCurrentPdfIndex] = useState(0);

  const fetchCourse = useCallback(async () => {
    try {
      const { data } = await API.get(`/courses/${courseId}`);
      setCourse(data);
    } catch (error) {
      alert("Error fetching course");
    }
  }, [courseId]);

  useEffect(() => {
    fetchCourse();
  }, [fetchCourse]);

  const handleNext = () => {
    if (currentPdfIndex < course.pdfFiles.length - 1) {
      setCurrentPdfIndex(currentPdfIndex + 1);
    }
  };

  const handlePrevious = () => {
    if (currentPdfIndex > 0) {
      setCurrentPdfIndex(currentPdfIndex - 1);
    }
  };

  if (!course) {
    return (
      <Container>
        <Typography variant="h5" sx={{ mt: 8 }}>
          Loading course...
        </Typography>
      </Container>
    );
  }

  return (
    <Container maxWidth="md" sx={{ mt: 8 }}>
      <Typography variant="h4" gutterBottom>
        {course.title}
      </Typography>
      <Typography variant="body1" gutterBottom>
        {course.description}
      </Typography>
      <Box
        sx={{
          mt: 4,
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
        }}
      >
        <iframe
          src={course.pdfFiles[currentPdfIndex]}
          title={`PDF ${currentPdfIndex + 1}`}
          style={{
            width: "100%",
            height: "500px",
            border: "1px solid #ddd",
            marginBottom: "20px",
          }}
        ></iframe>
        <Box sx={{ display: "flex", justifyContent: "space-between", width: "100%" }}>
          <Button
            variant="outlined"
            color="primary"
            disabled={currentPdfIndex === 0}
            onClick={handlePrevious}
          >
            Previous
          </Button>
          <Button
            variant="outlined"
            color="primary"
            disabled={currentPdfIndex === course.pdfFiles.length - 1}
            onClick={handleNext}
          >
            Next
          </Button>
        </Box>
      </Box>
    </Container>
  );
};

export default CourseViewer;
