import React, { useEffect, useState } from 'react';
import {
  Container, Typography, Button, Table, TableBody, TableCell,
  TableContainer, TableHead, TableRow, Paper
} from '@mui/material';
import AddDeviceDialog from './AddDeviceDialog';
import EditDeviceDialog from './EditDeviceDialog';
import DeviceChart from './DeviceChart';

function App() {
  const [devices, setDevices] = useState([]);
  const [selectedDevice, setSelectedDevice] = useState(null);

  useEffect(() => {
    fetch('http://localhost:5001/api/devices')
      .then(res => res.json())
      .then(setDevices)
      .catch(err => console.error('Error fetching devices:', err));
  }, []);

  const handleAddDevice = (deviceData) => {
    fetch('http://localhost:5001/api/devices', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(deviceData),
    })
    .then(res => res.json())
    .then(data => {
      setDevices([...devices, data]);
    })
    .catch(error => console.error('Error adding new device:', error));
  };

  const handleUpdateDevice = (updatedDevice) => {
    fetch(`http://localhost:5001/api/devices/${updatedDevice.Did}`, {
      method: 'PUT',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(updatedDevice),
    })
    .then(res => res.json())
    .then(() => {
      const updatedDevices = devices.map(device => device.Did === updatedDevice.Did ? updatedDevice : device);
      setDevices(updatedDevices);
    })
    .catch(error => console.error('Error updating device:', error));
  };

  return (
    <Container maxWidth="md" sx={{ marginTop: 4 }}>
      <Typography variant="h4" gutterBottom>Device Management System</Typography>
      <Button variant="contained" sx={{ mb: 2 }} onClick={() => setSelectedDevice({})}>Add Device</Button>
      <TableContainer component={Paper}>
        <Table>
          <TableHead>
            <TableRow>
              <TableCell>Device Name</TableCell>
              <TableCell>Water Flow (L/s)</TableCell>
              <TableCell>Pressure (psi)</TableCell>
              <TableCell>Temperature (Â°C)</TableCell>
              <TableCell>Edit</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {devices.map((device) => (
              <TableRow key={device.Did}>
                <TableCell>{device.name}</TableCell>
                <TableCell>{device.waterFlow}</TableCell>
                <TableCell>{device.pressure}</TableCell>
                <TableCell>{device.temperature}</TableCell>
                <TableCell>
                  <Button color="primary" onClick={() => setSelectedDevice(device)}>Edit</Button>
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
      <DeviceChart devices={devices} />
      <AddDeviceDialog open={!!selectedDevice && !selectedDevice.Did} onClose={() => setSelectedDevice(null)} onAdd={handleAddDevice} />
      {selectedDevice && selectedDevice.Did && <EditDeviceDialog device={selectedDevice} onClose={() => setSelectedDevice(null)} onUpdate={handleUpdateDevice} />}
    </Container>
  );
}

export default App;
