import React, { useState, useEffect } from 'react';
import {
  Box,
  Typography,
  CircularProgress,
  Button
} from '@mui/material';
import { Line, Bar, Pie } from 'react-chartjs-2';
import { Chart as ChartJS, CategoryScale, LinearScale, PointElement, LineElement, BarElement, ArcElement, Title, Tooltip, Legend } from 'chart.js';

ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, BarElement, ArcElement, Title, Tooltip, Legend);

function DeviceAnalytics({ deviceId }) {
  const [loading, setLoading] = useState(true);
  const [chartData, setChartData] = useState({});
  const [chartType, setChartType] = useState('line'); // 'line', 'bar', 'pie'

  useEffect(() => {
    const fetchData = async () => {
      setLoading(true);
      try {
        const response = await fetch(`http://localhost:5001/api/devices/${deviceId}`);
        const device = await response.json();
        const labels = Array.from(new Array(24), (_, index) => `${index} hour`); // Labels for 24 hours, adjust as needed

        // Ensure data arrays are valid or default to empty arrays
        const waterFlowData = device.waterFlow || [];
        const pressureData = device.pressure || [];
        const temperatureData = device.temperature || [];

        const data = {
          labels,
          datasets: [
            {
              label: 'Water Flow (L/s)',
              data: waterFlowData,
              borderColor: 'rgb(75, 192, 192)',
              backgroundColor: 'rgba(75, 192, 192, 0.5)',
            },
            {
              label: 'Pressure (psi)',
              data: pressureData,
              borderColor: 'rgb(255, 99, 132)',
              backgroundColor: 'rgba(255, 99, 132, 0.5)',
            },
            {
              label: 'Temperature (°C)',
              data: temperatureData,
              borderColor: 'rgb(53, 162, 235)',
              backgroundColor: 'rgba(53, 162, 235, 0.5)',
            }
          ]
        };
        setChartData(data);
      } catch (error) {
        console.error("Error fetching device data:", error);
      }
      setLoading(false);
    };

    fetchData();
  }, [deviceId]); // Dependency on deviceId to refetch when it changes

  const changeChart = (type) => {
    setChartType(type);
  };

  if (loading) {
    return <Box sx={{ display: 'flex', justifyContent: 'center' }}><CircularProgress /></Box>;
  }

  return (
    <Box>
      <Typography variant="h6" gutterBottom>
        Device Analytics: {deviceId}
      </Typography>

      {/* Buttons to switch charts */}
      <Button variant="contained" onClick={() => changeChart('line')} sx={{ mr: 1 }}>Line Chart</Button>
      <Button variant="contained" onClick={() => changeChart('bar')} sx={{ mr: 1 }}>Bar Chart</Button>
      <Button variant="contained" onClick={() => changeChart('pie')}>Pie Chart</Button>

      {/* Conditionally render charts */}
      {chartType === 'line' && <Line data={chartData} />}
      {chartType === 'bar' && <Bar data={chartData} />}
      {chartType === 'pie' && <Pie data={chartData} />}
    </Box>
  );
}

export default DeviceAnalytics;
