import React, { useEffect, useState } from 'react';
import {
  Container,
  Typography,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Button,
  Slider,
  Box,
  TextField,
  Stack
} from '@mui/material';

function App() {
  const [devices, setDevices] = useState([]);
  const [openDialog, setOpenDialog] = useState(false);
  const [selectedDevice, setSelectedDevice] = useState(null);

  useEffect(() => {
    fetch('http://localhost:5001/api/devices')
      .then(res => res.json())
      .then(setDevices)
      .catch(err => console.error('Error fetching devices:', err));
  }, []);

  const handleDialogOpen = (device) => {
    setSelectedDevice(device);
    setOpenDialog(true);
  };

  const handleDialogClose = () => {
    setOpenDialog(false);
  };

  const handleSliderChange = (field, newValue) => {
    setSelectedDevice(prev => ({
      ...prev,
      [field]: [...prev[field], newValue].slice(-10) // Maintain last 10 records
    }));
  };

  const handleAddDevice = () => {
    if (!newDevice.Did) {
      alert("Please fill in all required fields.");
      return;
    }

    const deviceToAdd = {
      ...newDevice,
      waterFlow: [newDevice.waterFlow],
      pressure: [newDevice.pressure],
      temperature: [newDevice.temperature]
    };

    fetch('http://localhost:5001/api/devices', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(deviceToAdd),
    })
    .then(response => response.json())
    .then(data => {
      console.log('Add device response:', data);
      setDevices([...devices, data]);
      handleCloseAddDialog();
    })
    .catch(error => {
      console.error('Error adding new device:', error);
      alert('Failed to add device: ' + error.message);
    });
  };

  const [newDevice, setNewDevice] = useState({
    Did: '',
    waterFlow: 0,
    pressure: 0,
    temperature: 0,
  });

  const handleNewDeviceFieldChange = (e) => {
    const { name, value } = e.target;
    setNewDevice(prev => ({
      ...prev,
      [name]: parseFloat(value)
    }));
  };

  return (
    <Container maxWidth="md" sx={{ marginTop: 4 }}>
      <Typography variant="h4" gutterBottom>
        Water Management System
      </Typography>
      <Button variant="contained" sx={{ mb: 2 }} onClick={() => setOpenAddDialog(true)}>
        Add Device
      </Button>
      <TableContainer component={Paper}>
        <Table>
          <TableHead>
            <TableRow>
              <TableCell>Device ID</TableCell>
              <TableCell>Latest Water Flow (L/s)</TableCell>
              <TableCell>Latest Pressure (psi)</TableCell>
              <TableCell>Latest Temperature (°C)</TableCell>
              <TableCell>Edit</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {devices.map((device) => (
              <TableRow key={device.Did} hover style={{ cursor: 'pointer' }} onClick={() => handleDialogOpen(device)}>
                <TableCell>{device.Did}</TableCell>
                <TableCell>{device.waterFlow.slice(-1)}</TableCell>
                <TableCell>{device.pressure.slice(-1)}</TableCell>
                <TableCell>{device.temperature.slice(-1)}</TableCell>
                <TableCell>
                  <Button onClick={() => handleDialogOpen(device)}>Edit</Button>
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
      {openDialog && selectedDevice && (
        <Dialog open={openDialog} onClose={handleDialogClose} fullWidth maxWidth="sm">
          <DialogTitle>Edit Device Data</DialogTitle>
          <DialogContent>
            <Typography gutterBottom>Device ID: {selectedDevice.Did}</Typography>
            <Box sx={{ mt: 2 }}>
              <Typography>Water Flow (L/s)</Typography>
              <Slider
                value={selectedDevice.waterFlow.slice(-1)[0] || 0}
                min={0}
                max={500}
                step={1}
                onChange={(e, val) => handleSliderChange('waterFlow', val)}
              />
            </Box>
            <Box sx={{ mt: 2 }}>
              <Typography>Pressure (psi)</Typography>
              <Slider
                value={selectedDevice.pressure.slice(-1)[0] || 0}
                min={0}
                max={150}
                step={1}
                onChange={(e, val) => handleSliderChange('pressure', val)}
              />
            </Box>
            <Box sx={{ mt: 2 }}>
              <Typography>Temperature (°C)</Typography>
              <Slider
                value={selectedDevice.temperature.slice(-1)[0] || 0}
                min={-20}
                max={100}
                step={1}
                onChange={(e, val) => handleSliderChange('temperature', val)}
              />
            </Box>
          </DialogContent>
          <DialogActions>
            <Button onClick={handleDialogClose}>Close</Button>
          </DialogActions>
        </Dialog>
      )}
      <Dialog open={openAddDialog} onClose={handleCloseAddDialog} fullWidth maxWidth="sm">
        <DialogTitle>Add New Device</DialogTitle>
        <DialogContent>
          <Stack spacing={2}>
            <TextField
              label="Device ID"
              fullWidth
              variant="standard"
              value={newDevice.Did}
              onChange={e => handleNewDeviceFieldChange(e)}
              name="Did"
            />
            <TextField
              label="Water Flow (L/s)"
              fullWidth
              type="number"
              variant="standard"
              value={newDevice.waterFlow}
              onChange={e => handleNewDeviceFieldChange(e)}
              name="waterFlow"
            />
            <TextField
              label="Pressure (psi)"
              fullWidth
              type="number"
              variant="standard"
              value={newDevice.pressure}
              onChange={e => handleNewDeviceFieldChange(e)}
              name="pressure"
            />
            <TextField
              label="Temperature (°C)"
              fullWidth
              type="number"
              variant="standard"
              value={newDevice.temperature}
              onChange={e => handleNewDeviceFieldChange(e)}
              name="temperature"
            />
          </Stack>
        </DialogContent>
        <DialogActions>
          <Button onClick={handleCloseAddDialog}>Cancel</Button>
          <Button onClick={handleAddDevice}>Add</Button>
        </DialogActions>
      </Dialog>
    </Container>
  );
}

export default App;
