import React, { useState, useEffect } from 'react';
import {
  Box,
  Typography,
  CircularProgress,
  Button,
  Slider
} from '@mui/material';
import { Line, Bar, Pie } from 'react-chartjs-2';
import { Chart as ChartJS, CategoryScale, LinearScale, PointElement, LineElement, BarElement, ArcElement, Title, Tooltip, Legend } from 'chart.js';

ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, BarElement, ArcElement, Title, Tooltip, Legend);

function DeviceAnalytics({ deviceId }) {
  const [loading, setLoading] = useState(true);
  const [chartData, setChartData] = useState({});
  const [chartType, setChartType] = useState('line'); // 'line', 'bar', 'pie'
  const [dataRange, setDataRange] = useState([0, 50]); // Default range for data points

  useEffect(() => {
    setLoading(true);
    fetch(`http://localhost:5001/api/devices/${deviceId}`)
      .then(res => res.json())
      .then(device => {
        processChartData(device);
        setLoading(false);
      })
      .catch(error => {
        console.error("Error fetching device data:", error);
        setLoading(false);
      });
  }, [deviceId, dataRange]); // React to changes in deviceId and dataRange

  const processChartData = (device) => {
    // Assuming data points are already sorted by timestamps
    const maxDataLength = Math.min(device.waterFlow.length, device.pressure.length, device.temperature.length);
    const adjustedMaxDataRange = Math.min(dataRange[1], maxDataLength);

    const labels = device.waterFlow.slice(dataRange[0], adjustedMaxDataRange).map((_, index) => index + dataRange[0] + 1);

    const data = {
      labels,
      datasets: [
        {
          label: 'Water Flow (L/s)',
          data: device.waterFlow.slice(dataRange[0], adjustedMaxDataRange),
          borderColor: 'rgb(75, 192, 192)',
          backgroundColor: 'rgba(75, 192, 192, 0.5)',
        },
        {
          label: 'Pressure (psi)',
          data: device.pressure.slice(dataRange[0], adjustedMaxDataRange),
          borderColor: 'rgb(255, 99, 132)',
          backgroundColor: 'rgba(255, 99, 132, 0.5)',
        },
        {
          label: 'Temperature (°C)',
          data: device.temperature.slice(dataRange[0], adjustedMaxDataRange),
          borderColor: 'rgb(53, 162, 235)',
          backgroundColor: 'rgba(53, 162, 235, 0.5)',
        }
      ]
    };
    setChartData(data);
  };

  const handleRangeChange = (event, newValue) => {
    setDataRange(newValue);
  };

  if (loading) {
    return <Box sx={{ display: 'flex', justifyContent: 'center' }}><CircularProgress /></Box>;
  }

  return (
    <Box>
      <Typography variant="h6" gutterBottom>
        Device Analytics: {deviceId}
      </Typography>

      {/* Range Slider for selecting data points */}
      <Box sx={{ width: '80%', margin: '20px auto' }}>
        <Typography gutterBottom>Select Data Range:</Typography>
        <Slider
          value={dataRange}
          onChange={handleRangeChange}
          valueLabelDisplay="auto"
          min={0}
          max={Math.max(50, Math.min(device.waterFlow.length, device.pressure.length, device.temperature.length))}
          marks
          step={1}
          disableSwap
        />
      </Box>

      {/* Buttons to switch charts */}
      <Button variant="contained" onClick={() => setChartType('line')} sx={{ mr: 1 }}>Line Chart</Button>
      <Button variant="contained" onClick={() => setChartType('bar')} sx={{ mr: 1 }}>Bar Chart</Button>
      <Button variant="contained" onClick={() => setChartType('pie')}>Pie Chart</Button>

      {/* Conditionally render charts */}
      {chartType === 'line' && <Line data={chartData} />}
      {chartType === 'bar' && <Bar data={chartData} />}
      {chartType === 'pie' && <Pie data={chartData} />}
    </Box>
  );
}

export default DeviceAnalytics;
