import React, { useState, useEffect } from 'react';
import {
  Box,
  Typography,
  CircularProgress
} from '@mui/material';
import { Line } from 'react-chartjs-2';
import { Chart as ChartJS, CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend } from 'chart.js';

ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend);

function DeviceAnalytics({ deviceId }) {
  const [loading, setLoading] = useState(true);
  const [chartData, setChartData] = useState({});

  useEffect(() => {
    const fetchData = async () => {
      setLoading(true);
      try {
        const response = await fetch(`http://localhost:5001/api/devices/${deviceId}`);
        const device = await response.json();

        const labels = device.timestamps || Array.from(new Array(24), (_, index) => index); // Assuming timestamps array is available
        const waterFlowData = device.waterFlow || [];
        const pressureData = device.pressure || [];
        const temperatureData = device.temperature || [];

        const data = {
          labels,
          datasets: [
            {
              label: 'Water Flow (L/s)',
              data: waterFlowData,
              borderColor: 'rgb(75, 192, 192)',
              backgroundColor: 'rgba(75, 192, 192, 0.5)',
            },
            {
              label: 'Pressure (psi)',
              data: pressureData,
              borderColor: 'rgb(255, 99, 132)',
              backgroundColor: 'rgba(255, 99, 132, 0.5)',
            },
            {
              label: 'Temperature (°C)',
              data: temperatureData,
              borderColor: 'rgb(53, 162, 235)',
              backgroundColor: 'rgba(53, 162, 235, 0.5)',
            }
          ]
        };

        setChartData(data);
      } catch (error) {
        console.error("Error fetching device data:", error);
        // Handle error appropriately
      }
      setLoading(false);
    };

    fetchData();
  }, [deviceId]); // Reacting to changes in deviceId

  if (loading) {
    return <Box sx={{ display: 'flex', justifyContent: 'center' }}><CircularProgress /></Box>;
  }

  return (
    <Box>
      <Typography variant="h6" gutterBottom>
        Device Analytics: {deviceId}
      </Typography>
      <Line data={chartData} />
    </Box>
  );
}

export default DeviceAnalytics;
