import React, { useEffect, useState } from 'react';
import {
  Container,
  Typography,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Button,
  Slider,
  Box
} from '@mui/material';

/**
 * Example initial devices array.
 * In real life, you might fetch these from an API or a database.
 */
const initialDevices = [
  { Did: 'DEV-001', location: 'Pump Station A', waterFlow: 50, pressure: 80, temperature: 22 },
  { Did: 'DEV-002', location: 'Pump Station B', waterFlow: 60, pressure: 90, temperature: 24 },
  { Did: 'DEV-003', location: 'Reservoir West', waterFlow: 70, pressure: 100, temperature: 26 },
  { Did: 'DEV-004', location: 'Reservoir East', waterFlow: 80, pressure: 110, temperature: 28 },
];

function App() {
  const [devices, setDevices] = useState(initialDevices);

  // State for Dialog
  const [openDialog, setOpenDialog] = useState(false);
  const [selectedDeviceIndex, setSelectedDeviceIndex] = useState(null);

  // Open dialog for a clicked device
  const handleRowClick = (index) => {
    setSelectedDeviceIndex(index);
    setOpenDialog(true);
  };

  // Close the dialog
  const handleDialogClose = () => {
    setOpenDialog(false);
  };

  // Handle slider changes for the selected device
  const handleSliderChange = (field, newValue) => {
    setDevices((prevDevices) => {
      const updated = [...prevDevices];
      updated[selectedDeviceIndex] = {
        ...updated[selectedDeviceIndex],
        [field]: newValue
      };
      return updated;
    });
  };

  /**
   * useEffect for real-time simulation:
   * Every second:
   *  - randomize device data slightly
   *  - POST updated data array to the server
   */
  useEffect(() => {
    const interval = setInterval(() => {
      setDevices((prevDevices) => {
        // 1) Randomly tweak the data
        const updatedDevices = prevDevices.map((device) => ({
          ...device,
          waterFlow: parseFloat((device.waterFlow + (Math.random() * 2 - 1)).toFixed(2)),
          pressure: parseFloat((device.pressure + (Math.random() * 2 - 1)).toFixed(2)),
          temperature: parseFloat((device.temperature + (Math.random() * 2 - 1)).toFixed(2)),
        }));

        // 2) POST the updated data to the server
        fetch('http://localhost:5000/devices/bulk', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify(updatedDevices),
        })
          .then((res) => res.json())
          .then((data) => {
            // Optionally handle the server response
            console.log('Server response:', data);
          })
          .catch((error) => {
            console.error('Error posting devices:', error);
          });

        return updatedDevices;
      });
    }, 1000);

    // Cleanup interval on unmount
    return () => clearInterval(interval);
  }, []);

  // Convenience method for retrieving the device the user clicked
  const selectedDevice = selectedDeviceIndex !== null
    ? devices[selectedDeviceIndex]
    : null;

  return (
    <Container maxWidth="md" sx={{ marginTop: 4 }}>
      <Typography variant="h4" gutterBottom>
        Water Management System
      </Typography>

      <TableContainer component={Paper}>
        <Table>
          <TableHead>
            <TableRow>
              <TableCell><strong>Device ID</strong></TableCell>
              <TableCell><strong>Location</strong></TableCell>
              <TableCell><strong>Water Flow (L/s)</strong></TableCell>
              <TableCell><strong>Pressure (psi)</strong></TableCell>
              <TableCell><strong>Temperature (°C)</strong></TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {devices.map((device, index) => (
              <TableRow
                key={device.Did}
                hover
                style={{ cursor: 'pointer' }}
                onClick={() => handleRowClick(index)}
              >
                <TableCell>{device.Did}</TableCell>
                <TableCell>{device.location}</TableCell>
                <TableCell>{device.waterFlow}</TableCell>
                <TableCell>{device.pressure}</TableCell>
                <TableCell>{device.temperature}</TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>

      {/* Dialog for adjusting sensor fields */}
      <Dialog open={openDialog} onClose={handleDialogClose} fullWidth maxWidth="sm">
        <DialogTitle>Adjust Sensor Data</DialogTitle>
        <DialogContent dividers>
          {selectedDevice && (
            <>
              <Box sx={{ mt: 2 }}>
                <Typography gutterBottom>Water Flow (L/s)</Typography>
                <Slider
                  value={selectedDevice.waterFlow}
                  min={0}
                  max={500}
                  step={1}
                  onChange={(e, val) => handleSliderChange('waterFlow', val)}
                />
                <Typography variant="body2">
                  Current: {selectedDevice.waterFlow.toFixed(2)}
                </Typography>
              </Box>

              <Box sx={{ mt: 4 }}>
                <Typography gutterBottom>Pressure (psi)</Typography>
                <Slider
                  value={selectedDevice.pressure}
                  min={0}
                  max={150}
                  step={1}
                  onChange={(e, val) => handleSliderChange('pressure', val)}
                />
                <Typography variant="body2">
                  Current: {selectedDevice.pressure.toFixed(2)}
                </Typography>
              </Box>

              <Box sx={{ mt: 4 }}>
                <Typography gutterBottom>Temperature (°C)</Typography>
                <Slider
                  value={selectedDevice.temperature}
                  min={0}
                  max={100}
                  step={1}
                  onChange={(e, val) => handleSliderChange('temperature', val)}
                />
                <Typography variant="body2">
                  Current: {selectedDevice.temperature.toFixed(2)}
                </Typography>
              </Box>
            </>
          )}
        </DialogContent>
        <DialogActions>
          <Button onClick={handleDialogClose} variant="contained">
            Close
          </Button>
        </DialogActions>
      </Dialog>
    </Container>
  );
}

export default App;
